import React from 'react';
import { useForm } from 'react-hook-form';

const Login = () => {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);
  
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input type="text" placeholder="First name" {...register("name1", {required: true, maxLength: 8})} /> 
      {errors.name1 && errors.name1.type === "required" && <span className="error">This is required</span>}
      {errors.name1 && errors.name1.type === "maxLength" && <span className="error"className="error">Max length exceeded</span> }
       <br/>
      <input type="text" placeholder="Last name" {...register("Last name", {required: true, maxLength: 30})} />   <br/>
      <input type="text" placeholder="Email" {...register("Email", {required: true, pattern: /\S+@\S+\.\S+/})} />    <br/>
      <input type="tel" placeholder="Mobile number" {...register("Mobile number", {required: true, minLength: 6, maxLength: 12})} />   <br/>
      <input type="password" placeholder="Password" {...register("Password", {required: true, pattern: /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[#$@!%&*?])[A-Za-z\d#$@!%&*?]{8,30}$/, minLength: 8, maxLength: 30})} /> <br/>
      <span>Developer?</span>  <br/>
                                     <label htmlFor="Developer">1. Yes</label> <input  {...register("Developer", { required: true })} type="radio" value="Yes" /> <br/>
                                     <label htmlFor="Developer">2. No</label> <input {...register("Developer", { required: true })} type="radio" value="No" /> <br/>

      <input value="Submit" type="submit" />
    </form>
  );
}
export default Login;
