import React from 'react';
import { useHistory } from 'react-router-dom';
import './Meal.css'

const Meal = (props) => {
    const history = useHistory()
    const handleOrderNow =(idCategory)=> {
       history.push('/book/${idCategory}');
    }
    const meal = props.meal;
    const {strCategory, idCategory, strCategoryDescription, strCategoryThumb } = meal;
    return (
        <MDBCard style={{ maxWidth: '22rem' }}>
        <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
          <MDBCardImage src='https://mdbcdn.b-cdn.net/img/new/standard/nature/111.jpg' fluid alt='...' />
          <a>
            <div className='mask' style={{ backgroundColor: 'rgba(251, 251, 251, 0.15)' }}></div>
          </a>
        </MDBRipple>
        <MDBCardBody>
          <MDBCardTitle>Card title</MDBCardTitle>
          <MDBCardText>
            Some quick example text to build on the card title and make up the bulk of the card's content.
          </MDBCardText>
          <MDBBtn href='#'>Button</MDBBtn>
        </MDBCardBody>
      </MDBCard>
    );
};

export default Meal;
