import React from 'react';
import { MDBCard, MDBCardBody, MDBCardTitle, MDBCardText, MDBCardImage, MDBBtn, MDBRipple, MDBContainer } from 'mdb-react-ui-kit';


const Meal = (props) => {
    console.log(props.meal);
    const {idCategory, strCategory, strCategoryDescription, strCategoryThumb} = props.meal
    return (
        <div>
            <MDBContainer>
             <MDBCard style={{ maxWidth: '22rem' }}>
                <MDBRipple rippleColor='light' rippleTag='div' className='bg-image hover-overlay'>
                    <MDBCardImage src={strCategoryThumb} fluid alt='...' />
                    <a>
                    <div className='mask' style={{ backgroundColor: 'rgba(251, 251, 251, 0.15)' }}></div>
                    </a>
                </MDBRipple>
                <MDBCardBody>
                    <MDBCardTitle>{strCategory}</MDBCardTitle>
                    <MDBCardText>
                    {strCategoryDescription}
                    </MDBCardText>
                    <MDBBtn href='#'>Buy</MDBBtn>
                </MDBCardBody>
                </MDBCard>
      </MDBContainer>
        </div>
    );
};

export default Meal;
